import { useNavigate } from "react-router-dom";
import { useEffect } from "react";

const LOCAL_STORAGE_FORM_STATE_KEY = "isFormAlreadySubmitted";

const Authenticated = ({ childern }) => {
  const navigate = useNavigate();

  function onMount() {
    const formState = localStorage.getItem(LOCAL_STORAGE_FORM_STATE_KEY);
    const isFormAlreadySubmitted = JSON.parse(formState || "false");

    if (isFormAlreadySubmitted) navigate("/");
  }

  useEffect(onMount, []);
};

export default Authenticated;
