import { Form, Button } from "react-bootstrap";
// import LayoutSign from "../components/LayoutSign";
import { useNavigate } from "react-router-dom";
import "../assets/css/style.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Authenticated from "../auth/Authenticated";

const LOCAL_STORAGE_FORM_STATE_KEY = "isFormAlreadySubmitted";

const Login = () => {
  const navigate = useNavigate();

  function onSubmit(e) {
    e.preventDefault();

    localStorage.setItem(LOCAL_STORAGE_FORM_STATE_KEY, JSON.stringify(true));

    // Redirect to home
    navigate("/");
  }

  return (
    <Authenticated>
      <section className="Background-requirements">
        <div className="d-flex justify-content-center align-items-center vh-100">
          <div className="card p-5 fw-bold" style={{ width: "350px" }}>
            <h4
              className="mb-5 text-center"
              style={{ fontFamily: "Open Sans Bold" }}
            >
              LOGIN
            </h4>
            <Form onSubmit={onSubmit}>
              <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Control
                  type="text"
                  className="input-style"
                  placeholder="Insert your username"
                />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Control
                  type="password"
                  className="input-style"
                  placeholder="Insert your password"
                />
              </Form.Group>
              <Button
                className="w-100 text-light py-3 mb-3"
                variant="warning"
                type="submit"
                style={{ fontFamily: "Open Sans Bold", fontSize: "12px" }}
              >
                LOGIN
              </Button>
            </Form>
          </div>
        </div>
      </section>
    </Authenticated>
  );
};

export default Login;
