import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import opponents from "./Opponents";

function active(id) {
  let x = document.getElementById(id);
  // console.log(x.style.backgroundColor)
  if (x.style.backgroundColor === "orange") {
    x.style.backgroundColor = "rgb(33, 35, 37)";
  } else {
    x.style.backgroundColor = "orange";
  }
}

const Level = () => {
  const [value, setValue] = useState(["GRAND MASTER"]);
  const levels = [
    "NOVICE",
    "CLASS A",
    "CLASS B",
    "CLASS C",
    "CLASS D",
    "CANDIDATE MASTER",
    "GRAND MASTER",
  ];

  //   const nyoba = opponents.filter((e) => e.level === levels[6]);

  //   console.log(nyoba);
  return (
    <div className="d-flex justify-content-between">
      {levels.map((e, i) => {
        return (
          <Link
            className="candidate"
            id={e}
            to=""
            style={e == value ? { background: "orange" } : {}}
            onClick={(item) => {
              item.preventDefault();
              const arrIndex = value.indexOf(e);
              if (arrIndex !== -1) {
                setValue(value.filter((item) => item !== i));
              } else {
                setValue([...value, i]);
                console.log("terpanggil");
              }
              active(i);
            }}
          >
            {e}
          </Link>
        );
        console.log(e);
        // console.log(i);
      })}
    </div>
  );
};

export default Level;
