import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import opponents from "./Opponents";

function buttonActive(id) {
  let x = document.getElementById(id);
  // console.log(x.style.backgroundColor)
  if (x.style.backgroundColor === "rgb(255, 181, 69)") {
    x.style.backgroundColor = "rgb(33, 35, 37)";
  } else {
    x.style.backgroundColor = "rgb(255, 181, 69)";
  }
}

const level = () => {
  const levels = [
    "NOVICE",
    "CLASS A",
    "CLASS B",
    "CLASS C",
    "CLASS D",
    "CANDIDATE MASTER",
    "GRAND MASTER",
  ];

  let [arr, setArr] = useState(["GRAND MASTER"]);
  let [data, setData] = useState(opponents);

  useEffect(() => {
    setData(opponents.filter((item) => arr.includes(item.level)));
    // console.log("array use EF")
    // console.log(arr)
  }, [arr]);

  //   const nyoba = opponents.filter((e) => e.level === levels[6]);

  //   console.log(nyoba);
  return (
    <div className="d-flex justify-content-between">
      {/* {levels.map((e, i) => {
        return (
          <Link className="candidate" to="">
            {e}
          </Link>
        );
        console.log(e);
        // console.log(i);
      })} */}
      {levels.map((i, index) => (
        <Link
          to="#"
          id={index}
          key={index}
          className="btn fw-bold py-4 px-4 text-white"
          style={index === 6 ? { backgroundColor: "rgb(255, 181, 69)" } : {}}
          onClick={(e) => {
            e.preventDefault();
            const arrIndex = arr.indexOf(i);
            if (arrIndex !== -1) {
              setArr(arr.filter((item) => item !== i));
            } else {
              setArr([...arr, i]);
              console.log("terpanggil");
            }
            buttonActive(index);
          }}
        >
          {i}
        </Link>
      ))}
    </div>
  );
};

export default level;
